def solution(n):
    n = int(n)
    cycleCount = 0

    if n == 0:
        return 1

    while n != 1:
      print(n & 1)
      if n & 1: #TRUE if the number is odd, bitwise AND
        if n & 2 and n != 3: #TRUE if n is not 3 and is even
            n = n + 1
        else:
            n = n - 1
      else:
          n = n // 2

      cycleCount = cycleCount + 1

    return cycleCount


